import React from 'react';
import { useSrate } from 'react';
import MySlider from './MySlider';

function MyRGBPanel() {
    const panelCSS = {
        display: "flex",
        flexDirection:"column",
        border: "1px solid black",
        borderRadius: "10px",
        width: "300px",
        padding:"30px",
        alignItems:"center",
        margin:"autu",
        justifyContent:"center"
    };
    const[red, setred]=useSrate(128);
    const[green, setgreen]=useSrate(128);
    const[blue, setblue]=useSrate(128);
    return (
        <>

            <div style={panelCSS} >
                <h3>MyRGBPanel元件</h3>
                <div
                    style={{ backgroundColor: "red", width: "200px", height: "100px" }}
                />
                <MySlider color="RED" />
                <MySlider color="GREEN" />
                <MySlider color="BLUE" />
            </div>
        </>
    );
}

export default MyRGBPanel;
